var _ = require('lodash')._;

exports.ApiError = function(errorCode, errorDescription, statusCode, errorUri) {
    this.errorCode = errorCode;
    this.errorDescription = errorDescription;
    this.statusCode = statusCode ? statusCode : (errorCode === 'unauthorized' ? 401 : 400);
    this.errorUri = errorUri;
};
_.extend(exports.ApiError.prototype, Error.prototype);
