var crypto = require('crypto');
var Signer = module.exports = function(secretKey, algorithm) {
    if (typeof algorithm === 'undefined') {
        algorithm = 'sha256';
    }
    this.secretKey = secretKey;
    this.algorithm = algorithm;
};
Signer.prototype.sign = function(input) {
    var hmac = crypto.createHmac(this.algorithm, this.secretKey);
    hmac.write(input);
    return hmac.digest('base64');
};