var SignerRegistry = module.exports = function() {
    this.signers = {};
};
SignerRegistry.prototype.add = function(signer) {
    this.signers[signer.getType()] = signer;
};
SignerRegistry.prototype.get = function(type) {
    if (typeof this.signers[type] === 'undefined') {
        throw new Error('Signer of this type not found: ' + type);
    }
    return this.signers[type];
};