var models = require('../../model/models');

var CredentialsRepository = module.exports = function(credentialsObject) {
    this.credentialsObject = credentialsObject;
};

CredentialsRepository.prototype.load = function(id, callback) {
    if (typeof this.credentialsObject[id] !== 'undefined') {
        callback(null, new models.LoginCredentialsCollection([this.credentialsObject[id]]).at(0));
    } else {
        callback(new Error('No such id'));
    }
};